import React, { Component } from 'react';
import axios from 'axios';
import {Button, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Input, Label} from 'reactstrap';

const url = "https://jsonplaceholder.typicode.com/todos"

export class TaskM extends Component {
    
    constructor(props) {
        super(props)

        this.state = { 
            todo: [],
            modalInsertar: false,
            tipoModal: "",
            form: {
                id: "",
                title: "",
              },
        };
    }

    modalInsertar=()=>{
        this.setState({modalInsertar: !this.state.modalInsertar});
    }

    handleChange= async e=>{
        e.persist();
        await this.setState({
            form: {
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
        console.log(this.state.form);
    }

    seleccionarTask=(todo)=>{
        this.setState({
            tipoModal: "actualizar",
            form: {
                id: todo.id,
                title: todo.title
            }
        })
    }

    peticionGet=()=> {
        axios.get(url).then(response=>{
            this.setState({todo: response.data});
        }).catch(error=>{
            console.log(error.message);
          })
    }

     peticionPost= async ()=>{
         await axios.post(url, this.state.form).then(response=>{
           this.modalInsertar();
           this.peticionGet();
           console.log(response);
         }).catch(error=>{
           console.log(error.message);
         })
     }
    
    componentDidMount() {
        this.peticionGet();
    }

    render() {
        const {form} = this.state;
        return (
            <div>
                <div className="App">
                    <br />
                    <Button className="btn btn-success" 
                    onClick={()=>{this.setState({form: null, tipoModal: "insertar"}); this.modalInsertar()}}>Agregar Task</Button>
                    <br /><br />
                    <table className="table">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>TASK</th>
                                <th>ACCIONES</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.todo.map((userID,)=>{
                                return(
                                    <tr>
                                        <td>{userID.id}</td>
                                        <td>{userID.title}</td>
                                        <td>
                                            <button className="btn btn-secondary" onClick={()=>this.seleccionarTask(userID)}>Editar</button>
                                            {"   "}
                                            <button className="btn btn-danger">Eliminar</button>
                                        </td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                </div>

                <Modal isOpen={this.state.modalInsertar}>
                    <ModalHeader>
                    Nueva Task
                    </ModalHeader>
                    <ModalBody>
                    <FormGroup>
                        <Label htmlFor="id">ID</Label>
                        <Input 
                        className="form-control" 
                        type="text" 
                        name="id"  
                        id= "id"
                        readOnly 
                        onChange={this.handleChange}
                        value={form?form.id: this.state.todo.length+1}/>
                        <br />
                        <Label htmlFor="title">Task</Label>
                        <Input 
                        className="form-control" 
                        type="text" 
                        name="title" 
                        id="title"
                        onChange={this.handleChange}
                        value={form?form.title: ""}/> 
                    </FormGroup>
                    </ModalBody>

                    <ModalFooter>
                        {this.state.tipoModal == "insertar"?
                        <Button className="btn btn-success" onClick={()=>this.peticionPost()}>Agregar</Button>:
                        <Button className="btn btn-success">Actualizar</Button>
                        }
                        <Button className="btn btn-danger" onClick={()=>this.modalInsertar()}>Cancelar</Button>
                    </ModalFooter>
                </Modal>
            </div>
        )
    }
}

export default TaskM
