import React from 'react';
import {Button, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Input, Label} from 'reactstrap';
import './Modal.module.css';

class Ventana extends React.Component{
  state={
    abierto: false,
  }

  modalInsertar=()=>{
    this.setState({modalInsertar: !this.setState.modalInsertar});
}

  render(){
    return(
      <div>
        <Modal isOpen={this.state.modalInsertar} className="modalStyles">
            <ModalHeader>
            Nueva Task
            </ModalHeader>

            <ModalBody>
            <FormGroup>
                <Label htmlFor="id">ID</Label>
                <Input className="form-control" name="id" type="text" id="id" readOnly/>
                <br />
                <Label htmlFor="task">Task</Label>
                <Input className="form-control" name="id" type="text" id="task"/>
            </FormGroup>
            </ModalBody>

            <ModalFooter>
                <Button className="btn btn-success">Agregar</Button>
                <Button className="btn btn-danger" onClick={()=>this.modalInsertar()}>Cerrar</Button>
            </ModalFooter>
        </Modal>
      </div>
    )
  }
}

export default Ventana;
