import React, { Component } from 'react';
import axios from 'axios';
import {Button, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Input, Label} from 'reactstrap';

const url = "https://jsonplaceholder.typicode.com/todos"

export class TaskM extends Component {
    
    constructor(props) {
        super(props)

        this.state = { 
            todo: [],
            modalInsertar: false,
            form: {
                id: "",
                title: "",
              },
        };
    }

    modalInsertar=()=>{
        this.setState({modalInsertar: !this.state.modalInsertar});
    }

    handleChange= async e=>{
        e.persist();
        await this.setState({
            form: {
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
        console.log(this.state.form);
    }

    peticionGet=()=> {
        axios.get(url).then(response=>{
            this.setState({todo: response.data});
        }).catch(error=>{
            console.log(error.message);
          })
    }

    // peticionPost= async ()=>{
    //     await axios.post(url, this.state.form).then(response=>{
    //       this.modalInsertar();
    //       this.peticionGet();
    //       console.log(response);
    //     }).catch(error=>{
    //       console.log(error.message);
    //     })
    // }

    peticionPost = async () => {
        // preventDefault() we don't want to actually submit by default
        //e.preventDefault();

        const { title } = this.state;

        // Check for Errors
        if (title === "") {
            this.setState({ errors: { title: "Task is required" } });
            return;
        }

        // Construct a new contact object
        const newTask = {
            title,
        };

        // Getting external request via JSONplaceholder
        await axios
            .post("https://jsonplaceholder.typicode.com/users", 
            newTask);
            this.modalInsertar();
            this.peticionGet();
            console.log(response);

        // Clear State
        this.setState({
            title: "",
            errors: {}
        });
    };

    componentDidMount() {
        this.peticionGet();
    }

    render() {
        const {form} = this.state;
        return (
            <div>
                <div className="App">
                    <br />
                    <Button className="btn btn-success" onClick={()=>this.modalInsertar()}>Agregar Task</Button>
                    <br /><br />
                    <table className="table">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>TASK</th>
                                <th>ACCIONES</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.todo.map((userID,)=>{
                                return(
                                    <tr>
                                        <td>{userID.id}</td>
                                        <td>{userID.title}</td>
                                        <td>
                                            <button className="btn btn-secondary">Editar</button>
                                            {"   "}
                                            <button className="btn btn-danger">Eliminar</button>
                                        </td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                </div>

                <Modal isOpen={this.state.modalInsertar}>
                    <ModalHeader>
                    Nueva Task
                    </ModalHeader>
                    <ModalBody>
                    <FormGroup>
                        <Label htmlFor="id">ID</Label>
                        <Input 
                        className="form-control" 
                        type="text" 
                        name="id"  
                        readOnly 
                        onChange={this.handleChange}
                        value={this.state.todo.length+1}/>
                        <br />
                        <Label htmlFor="title">Task</Label>
                        <Input 
                        className="form-control" 
                        type="text" 
                        name="title" 
                        onChange={this.handleChange}
                        value={form.title}/> 
                    </FormGroup>
                    </ModalBody>

                    <ModalFooter>
                        <Button className="btn btn-success" onClick={()=>this.peticionPost()}>Agregar</Button>
                        <Button className="btn btn-danger" onClick={()=>this.modalInsertar()}>Cancelar</Button>
                    </ModalFooter>
                </Modal>
            </div>
        )
    }
}

export default TaskM
