import React from 'react';
import {Button, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Input, Label} from 'reactstrap';
import './Modal.module.css'

class App extends React.Component{
  state={
    abierto: false,
  }

  abrirModal=()=>{
    this.setState({abierto: !this.state.abierto});
  }

  render(){
    return(
      <>
      <Button className="btn btn-success" onClick={this.abrirModal}>Agregar Task</Button>

      <Modal isOpen={this.state.abierto} className="modalStyles">
        <ModalHeader>
          Nueva Task
        </ModalHeader>
        <ModalBody>
          <FormGroup>
            <Label htmlFor="id">ID</Label>
            <Input className="form-control" type="text" name="id" id="id" readOnly/>
            <br />
            <Label htmlFor="title">Task</Label>
            <Input type="text" id="title"/> 
          </FormGroup>
        </ModalBody>

        <ModalFooter>
            <Button className="btn btn-success">Agregar</Button>
            <Button className="btn btn-danger" onClick={this.abrirModal}>Cancelar</Button>
        </ModalFooter>
      </Modal>
      </>
    )
  }
}

export default App;
