import React, { Component } from 'react';
import axios from 'axios';
import {Button, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Input, Label} from 'reactstrap';


export class TaskM extends Component {
    
    constructor(props) {
        super(props)

        this.state = { 
            data: [],
            modalInsertar: false,
            form: {
                id: '',
                title: '',
              },
        };
    }

    modalInsertar=()=>{
        this.setState({modalInsertar: !this.state.modalInsertar});
    }

    handleChange= async e=>{
        e.persist();
        await this.setState({
            form: {
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
        console.log(this.state.form);
    }

    peticionGet=()=> {
        axios.get("https://jsonplaceholder.typicode.com/todos?_start=0&_limit=11").then(response=>{
            this.setState({data: response.data});
        }).catch(error=>{
            console.log(error.message);
          })
    }

    peticionPost= async ()=>{
        await axios.post("https://jsonplaceholder.typicode.com/todos?_start=0&_limit=11", this.state.form).then(_response=>{
          this.modalInsertar();
          this.peticionGet();
        }).catch(error=>{
          console.log(error.message);
        })
    }

    componentDidMount() {
        this.peticionGet();
    }

    render() {
        const {form} = this.state;
        return (
            <div>
                <div className="App">
                    <br />
                    <Button className="btn btn-success" onClick={()=>this.modalInsertar()}>Agregar Task</Button>
                    <br /><br />
                    <table className="table">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>TASK</th>
                                <th>ACCIONES</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.data.map((userID,)=>{
                                return(
                                    <tr>
                                        <td>{userID.id}</td>
                                        <td>{userID.title}</td>
                                        <td>
                                            <button className="btn btn-secondary">Editar</button>
                                            {"   "}
                                            <button className="btn btn-danger">Eliminar</button>
                                        </td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                </div>

                <Modal isOpen={this.state.modalInsertar}>
                    <ModalHeader>
                    Nueva Task
                    </ModalHeader>
                    <ModalBody>
                    <FormGroup>
                        <Label htmlFor="id">ID</Label>
                        <Input 
                        className="form-control" 
                        type="text" 
                        name="id"  
                        //readOnly 
                        onChange={this.handleChange}
                        value={form.id}/>
                        <br />
                        <Label htmlFor="title">Task</Label>
                        <Input 
                        className="form-control" 
                        type="text" 
                        name="title" 
                        onChange={this.handleChange}
                        value={form.title}/> 
                    </FormGroup>
                    </ModalBody>

                    <ModalFooter>
                        <Button className="btn btn-success" onClick={()=>this.peticionPost()}>Agregar</Button>
                        <Button className="btn btn-danger" onClick={()=>this.modalInsertar()}>Cancelar</Button>
                    </ModalFooter>
                </Modal>
            </div>
        )
    }
}

export default TaskM
