import React, { Component } from 'react';
import axios from 'axios';
import {Button, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Input, Label} from 'reactstrap';
//import '../Modal/Modal.module.css'

const url= "https://jsonplaceholder.typicode.com/todos"

export class TaskM extends Component {
    state = { 
        data: [],
        modalInsertar: false,
    };

    modalInsertar=()=>{
        this.setState({modalInsertar: !this.state.modalInsertar});
    }

    peticionGet=()=> {
        axios.get(url).then(response=>{
            this.setState({data: response.data});
        })
    }

    componentDidMount() {
        this.peticionGet();
    }

    render() {
        return (
            <div>
                <div className="App">
                    <br />
                    <Button className="btn btn-success" onClick={()=>this.modalInsertar()}>Agregar Task</Button>
                    <br /><br />
                    <table className="table">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>TASK</th>
                                <th>ACCIONES</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.data.map((userID,)=>{
                                return(
                                    <tr>
                                        <td>{userID.id}</td>
                                        <td>{userID.title}</td>
                                        <td>
                                            <button className="btn btn-secondary">Editar</button>
                                            {"   "}
                                            <button className="btn btn-danger">Eliminar</button>
                                        </td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                </div>

                <Modal isOpen={this.state.modalInsertar}>
                    <ModalHeader>
                    Nueva Task
                    </ModalHeader>
                    <ModalBody>
                    <FormGroup>
                        <Label htmlFor="id">ID</Label>
                        <Input className="form-control" type="text" name="id" id="id" readOnly/>
                        <br />
                        <Label htmlFor="title">Task</Label>
                        <Input className="form-control" type="text" name="title" id="id"/> 
                    </FormGroup>
                    </ModalBody>

                    <ModalFooter>
                        <Button className="btn btn-success">Agregar</Button>
                        <Button className="btn btn-danger" onClick={()=>this.modalInsertar()}>Cancelar</Button>
                    </ModalFooter>
                </Modal>
            </div>
        )
    }
}

export default TaskM
